sortdist* SortSystem::sortExtension(std::filesystem::path file, int index)
{
    SortArchive dat = data[index];
    std::string fext = file.extension().string();
    for (size_t i = 0; i < dat.criterias.size(); i++)
    {
        std::stringstream ss(dat.criterias[i]);
        while (ss.good()) {
            std::string t;
            std::getline(ss, t, ',');
            if (fext.compare(t) == 0) {
                return &dat.routes[i];
            }
        }
    }
    return &dat.routes[dat.routes.size() - 1];
}
